```
// ============================
// SEARCH + "DID YOU MEAN" (offline)
// ============================

let _searchIndex = [];
let _lastHighlight = null;

function normalizeText(s){
  return (s || "")
    .toLowerCase()
    .normalize("NFD").replace(/[\u0300-\u036f]/g, ""); // remove accents
}

// Levenshtein distance (edit distance)
function levenshtein(a, b){
  a = normalizeText(a);
  b = normalizeText(b);
  const m = a.length, n = b.length;
  if (m === 0) return n;
  if (n === 0) return m;

  const dp = new Array(n + 1);
  for (let j = 0; j <= n; j++) dp[j] = j;

  for (let i = 1; i <= m; i++){
    let prev = dp[0];
    dp[0] = i;
    for (let j = 1; j <= n; j++){
      const temp = dp[j];
      const cost = (a[i-1] === b[j-1]) ? 0 : 1;
      dp[j] = Math.min(
        dp[j] + 1,        // deletion
        dp[j-1] + 1,      // insertion
        prev + cost       // substitution
      );
      prev = temp;
    }
  }
  return dp[n];
}

// Score match:
// - exact substring hit => high score
// - token hits => medium
// - otherwise use did-you-mean via levenshtein => lower score
function scoreMatch(query, label){
  const q = normalizeText(query).trim();
  if (!q) return 0;

  const t = normalizeText(label);
  if (!t) return 0;

  if (t.includes(q)) return 100;

  const tokens = q.split(/\s+/).filter(Boolean);
  let tokenScore = 0;
  for (const tok of tokens){
    if (t.includes(tok)) tokenScore += 15;
  }
  if (tokenScore > 0) return 60 + tokenScore;

  // did-you-mean: compute edit distance to the whole label
  // Normalize by length so short queries don't dominate
  const dist = levenshtein(q, t);
  const maxLen = Math.max(q.length, t.length) || 1;
  const similarity = 1 - (dist / maxLen); // 0..1

  // only accept if reasonably similar
  if (similarity < 0.45) return 0;

  return Math.floor(similarity * 50); // 0..50
}

function buildSearchIndex(){
  const items = [];

  // index common visible texts
  const nodes = document.querySelectorAll(
    "h1,h2,h3,p,.card-title,.sim-overlay,.sub-card h3,.sub-card p,.video-title,.tutorial-title"
  );

  nodes.forEach(node => {
    const text = (node.textContent || "").trim();
    if (!text) return;
    const sec = node.closest("section");
    items.push({
      label: text,
      sectionId: sec ? sec.id : "landing",
      element: node
    });
  });

  // index landing cards -> direct navigation
  document.querySelectorAll(".card[data-target]").forEach(btn => {
    const titleEl = btn.querySelector(".card-title");
    const text = (titleEl ? titleEl.textContent : "Menu").trim();
    items.push({
      label: text,
      sectionId: "landing",
      element: btn,
      action: () => showSection(btn.getAttribute("data-target"))
    });
  });

  // index videos/tutorial cards if they exist
  document.querySelectorAll(".video-card").forEach(card => {
    const title = (card.querySelector(".video-title")?.textContent || "").trim();
    if (!title) return;
    const sec = card.closest("section");
    items.push({ label: title, sectionId: sec ? sec.id : "opcion6", element: card });
  });

  document.querySelectorAll(".tutorial-card").forEach(card => {
    const title = (card.querySelector(".tutorial-title")?.textContent || "").trim();
    if (!title) return;
    const sec = card.closest("section");
    items.push({ label: title, sectionId: sec ? sec.id : "tutorials", element: card });
  });

  _searchIndex = items;
}

function focusHit(hit){
  if (!hit) return;

  if (hit.action){
    hit.action();
  } else if (hit.sectionId){
    showSection(hit.sectionId);
  }

  setTimeout(() => {
    const el = hit.element;
    if (!el) return;

    if (_lastHighlight) _lastHighlight.classList.remove("highlight-hit");
    el.classList.add("highlight-hit");
    _lastHighlight = el;

    el.scrollIntoView({ behavior: "smooth", block: "center" });
  }, 50);
}

const searchInput = document.getElementById("searchInput");
const searchBtn = document.getElementById("searchBtn");
const suggestBox = document.getElementById("searchSuggest");

function hideSuggest(){
  suggestBox.classList.remove("show");
  suggestBox.innerHTML = "";
}

function renderSuggest(list, query){
  suggestBox.innerHTML = "";

  if (!list.length){
    // if nothing found, give a small message
    const div = document.createElement("div");
    div.className = "item";
    div.textContent = `No matches.`;
    suggestBox.appendChild(div);
    suggestBox.classList.add("show");
    return;
  }

  // If the top hit is only "did-you-mean" (score < 60), show hint
  if (list[0]._score < 60){
    const hint = document.createElement("div");
    hint.className = "item";
    hint.style.opacity = "0.75";
    hint.style.cursor = "default";
    hint.textContent = `Did you mean:`;
    suggestBox.appendChild(hint);
  }

  list.forEach(hit => {
    const div = document.createElement("div");
    div.className = "item";
    div.textContent = hit.label;
    div.addEventListener("click", () => {
      hideSuggest();
      focusHit(hit);
    });
    suggestBox.appendChild(div);
  });

  suggestBox.classList.add("show");
}

function doSearch(){
  const q = (searchInput.value || "").trim();
  if (!q){ hideSuggest(); return; }

  buildSearchIndex();

  const scored = _searchIndex
    .map(it => ({...it, _score: scoreMatch(q, it.label)}))
    .filter(it => it._score > 0)
    .sort((a,b) => b._score - a._score)
    .slice(0, 12);

  renderSuggest(scored, q);
}

if (searchInput && searchBtn && suggestBox){
  searchInput.addEventListener("input", doSearch);
  searchInput.addEventListener("keydown", (e) => {
    if (e.key === "Enter") doSearch();
    if (e.key === "Escape") hideSuggest();
  });

  searchBtn.addEventListener("click", doSearch);

  document.addEventListener("click", (e) => {
    if (!e.target.closest(".search-wrap")) hideSuggest();
  });
}
