function loadTutorial(id) {
    const raw = document.getElementById(id).textContent.trim();

    const parser = new DOMParser();
    const xml = parser.parseFromString(raw, "application/xml");

    // Comprobaci√≥n de errores XML
    const errorNode = xml.querySelector("parsererror");
    if (errorNode) {
        console.error("Error parsing tutorial XML:", errorNode.textContent);
        return null;
    }

    return {
        exec: xml.querySelector("Exec")?.textContent.trim(),
        title: xml.querySelector("Title")?.textContent.trim(),
        help: xml.querySelector("Help")?.innerHTML.trim(),
        xml
    };
}
