function renderTutorials() {
  const container = document.getElementById('tutorials-container');
  const rootHelpEl = document.getElementById('tutorials-root-help');
  if (!container || !rootHelpEl || !_tutorialsDoc) return;

  const root = _tutorialsDoc.getElementsByTagName('Tutoriales')[0] || _tutorialsDoc.documentElement;
  const rootHelpNode = root ? root.getElementsByTagName('Help')[0] : null;
  const rootHelp = textOf(rootHelpNode);
