```
// Node.js script para encriptar mhn.json y crear mhn.enc
// Requiere instalar crypto-js: npm install crypto-js

const CryptoJS = require('crypto-js');
const fs = require('fs');

// Uso: node encrypt_config.js inputfile outputfile key
const args = process.argv.slice(2);
if (args.length < 3) {
	console.error('Uso: node encrypt_config.js <inputfile> <outputfile> <key>');
	process.exit(1);
}
const [inputFile, outputFile, passphrase] = args;


let json = '';
if (inputFile.startsWith('reg:')) {
	// Leer del registro de Windows
	// Ejemplo: reg:HKEY_CURRENT_USER\\Software\\MyApp\\Config
	const regKey = inputFile.slice(4);
	try {
		const { execSync } = require('child_process');
		// Leer el valor por defecto de la clave
		const output = execSync(`reg query "${regKey}" /ve`, { encoding: 'utf8' });
		// Buscar la l√≠nea con REG_SZ y extraer el valor
		const match = output.match(/REG_SZ\s+(.*)/);
		if (match) {
			json = match[1];
		} else {
			throw new Error('No se encontr√≥ valor REG_SZ en la clave');
		}
	} catch (e) {
		console.error('Error leyendo del registro:', e.message);
		process.exit(1);
	}
} else {
	json = fs.readFileSync(inputFile, 'utf8');
}
const encrypted = CryptoJS.AES.encrypt(json, passphrase).toString();
fs.writeFileSync(outputFile, encrypted);

console.log('Archivo encriptado creado:', outputFile);
```
